import React, { Component } from 'react';
import moment from 'moment';


class Calender extends Component {
    constructor(props){
        super(props);

        this.state = {
            dateContext: moment(),
            today: moment(),
            showMonthPopup: false,
            showYearPopup: false
          
        }

        this.width = this.props.width || "350px";
        this.style = this.props.style || {};
        this.year = this.year.bind(this);
        this.month = this.month.bind(this);
        this.daysInMonth = this.daysInMonth.bind(this);
        this.currentDate = this.currentDate.bind(this);
        this.currentDay = this.currentDay.bind(this);
    }

    weekdays=moment.weekdays();
    weekdaysShort= moment.weekdaysShort();
    months = moment.months()

    year =()=>{
        return this.state.dateContext.format("Y");
    }
    month =()=>{
        return this.state.dateContext.format("MMM");
    }
    daysInMonth =()=>{
        return this.state.dateContext.daysInMonth();
    }
    currentDate = () =>{
        return this.state.dateContext.get('date')
    }
    currentDay = ()=>{
        return this.state.dateContext.format("D")
    }
    firstDayofMonth=()=>{
        let firstday = moment(this.state.dateContext).startOf('month').format('d')
        return firstday
    }

    setMonth =(month)=>{
        let monthNo= this.months.indexOf(month);
        let dateContext = Object.assign({}, this.state.dateContext)
        dateContext = moment(dateContext).set('month', monthNo)
        this.setState({
            dateContext
        })
    }
    onselectchange =(e,data) =>{
        this.setMonth(data)
        this.props.onmonthchange && this.props.onmonthchange()
    }
    selectlist=(props)=>{
        let popup = props.data.map((data) =>{
            return(
                <div key ={data}>
                    <a href = "#" onClick = {(e) => {this.onselectchange(e,data)}}>
                        {data}
                    </a>
                </div>
            )
        });
    
        return(
            <div className="month-popup">
                {popup}
            </div>
        )
    }
    nextMonth=()=>{
        let dateContext = Object.assign({},this.state.dateContext)
        dateContext = moment(dateContext).add(1,"month");
        this.setState({
            dateContext
        })
        this.props.onnextMonth && this.props.onnextMonth();
    }
    prevMonth=()=>{
        let dateContext = Object.assign({},this.state.dateContext)
        dateContext = moment(dateContext).subtract(1,"month");
        this.setState({
            dateContext
        })
        this.props.onprevMonth && this.props.onprevMonth();
    }
    onchangemonth=(e,month)=>{
        this.setState({
            showMonthPopup: !this.state.showMonthPopup
        })
    }
    MonthNav=()=>{
        return(
            <span className="label-month"
            onClick={(e) =>{this.onchangemonth(e,this.month())}}>
                {this.month()}
                {this.state.showMonthPopup && 
                <this.selectlist data={this.months}/>
                }
            </span>
        )
    }
    showyeareditor =() =>{
        this.setState({
            showyearnav:true
        })
    }
    setYear =(year) =>{
        let dateContext = Object.assign({}, this.state.dateContext);
        dateContext = moment(dateContext).set("year", year);
        this.setState({
            dateContext
        })
    }
    onyearchange =(e) =>{
        this.setYear(e.target.value);
        this.props.onyearchange && this.props.onyearchange(e, e.target.value)
    }
    onkeyupyear =(e)=>{
        if( e.which === 13 || e.which ===27){
            this.setYear(e.target.value);
            this.setState({
                showyearnav:false
            })
        }
    }
    YearNav =()=>{
        return(
            this.state.showyearnav ?
            <input
                 defaultValue = {this.year()}
                 className="editor-year"
                 ref={(yearinput) =>{this.yearinput = yearinput}}
                 onKeyUp = {(e) =>this.onkeyupyear(e)}
                 onChange={(e) => this.onyearchange(e)}
                 type="number"
                 placeholder="year"/>
            :
            <span className="label-year"
                onClick={(e) =>{this.showyeareditor()}}>
                {this.year()} 
            </span>
        )
    }
    ondayclick=(e,day) =>{
        this.props.ondayclick && this.props.ondayclick(e,day)
    }
    render(){
        let weekdays = this.weekdaysShort.map((day) =>{
            return(
                <td key={day} className="week-day">{day}</td>
            )
        })

        let blanks = [];
        for(let i=0; i< this.firstDayofMonth(); i++){
            blanks.push(<td className="emptySlots">
                {""}
            </td>)
        }
        console.log( "blanks: ", blanks)

        let daysInMonth = [];
        let her = "/"
        for (let d=1; d < this.daysInMonth() + 1 ; d++){
            let className= (d == this.currentDay() ? "day current-day": "day")
            daysInMonth.push(
                <td key={d} className={className}>
                    <span onClick={(e) =>{this.ondayclick(e,d)}}>
                        <a href={"content" + her + d} style={{color:"black"}}>{d}</a></span>
                </td>
            )
        }
        console.log("day", daysInMonth)

        var totalslots =[...blanks, ...daysInMonth];
        let rows = [];
        let cells = [];

        totalslots.forEach((row,i)=> {
            if((i % 7) != 0){
                cells.push(row)
            }else{
                let insertRow = cells.slice();
                rows.push(insertRow);
                cells = []
                cells.push(row)
            }
            if(i== totalslots.length -1){
                let insertrow = cells.slice();
                rows.push(insertrow)
            }
        })
        let trElems=rows.map((d,i) =>{
            return(
                <tr key={i*100}>
                    <span>{d}</span>
                </tr>
            )
            
        })
        
        return(
        <div>
            <div className="arch" style={{display:"inline"}}>
            <p className=" f3" style={{textAlign:"center"}}><b>Archives</b></p>
            </div>
        <div className="calendar-container">
            
            <table className="calendar">
                <thead>
                    <tr className="calendar-header">
                        <td colSpan="5" style={{fontSize:"15px", paddingTop:"15px", paddingLeft:"50px"}}>
                            <this.MonthNav/>
                            {" "}
                            <this.YearNav/>
                        </td>
                        <td colSpan="" className="nav-month" style={{marginRight:'16px'}}>
                            <i style={{fontSize: "10px"}} className="f6 pointer prev fa fa-fw fa-chevron-left"
                            onClick={(e) =>{this.prevMonth()}}></i>
                            <i style={{fontSize: "10px"}} className="f6 pointer prev fa fa-fw fa-chevron-right"
                            onClick={(e) =>{this.nextMonth()}}></i>

                        </td>
                    </tr>
                </thead>
                <tbody >
                    <tr> 
                    {weekdays}
                    </tr>
                    {trElems}
                </tbody>
            </table>
